INSERT INTO `acx_email_templates` (`name`, `module`, `subject`, `body`, `variables`) VALUES
('', '', '', NULL, NULL),
('billed', 'invoicing', 'Invoice #:invoice_number has been billed', '<p>Hello,</p>\r\n<p><a href=":closed_by_url">:closed_by_name</a> just marked invoice <b>#:invoice_number</b> as billed. Access <a href=":invoice_url">invoice details and payments here</a>.</p>\r\n<p>Thank You,<br />:owner_company_name</p>', 'closed_by_name\nclosed_by_url\ninvoice_number\ninvoice_url'),
('cancel', 'invoicing', 'Invoice #:invoice_number has been canceled', '<p>Hello,</p>\r\n<p><a href=":closed_by_url">:closed_by_name</a> just canceled invoice <b>#:invoice_number</b>. Access <a href=":invoice_url">invoice details here</a>.</p>\r\n<p>Thank You,<br />:owner_company_name</p>', 'closed_by_name\nclosed_by_url\ninvoice_number\ninvoice_url'),
('forgot_password', 'system', 'Reset your password', '<p>Hello,</p>\r\n<p>Visit <a href=":reset_url">this page</a> to reset your password. This page will be valid for 2 days!</p>\r\n<p>Thank You,<br />:owner_company_name</p>', 'owner_company_name\nreset_url'),
('issue', 'invoicing', 'Invoice #:invoice_number has been issued', '<p>Hello,</p>\r\n<p><a href=":issued_by_url">:issued_by_name</a> just issued invoice <b>#:invoice_number</b> to you. Access <a href=":invoice_url">invoice details here</a> or <a href=":pdf_url">download PDF version here</a>.</p>\r\n<p>Thank You,<br />:owner_company_name</p>', 'issued_by_name\nissued_by_url\ninvoice_number\ninvoice_url\npdf_url'),
('new_comment', 'resources', '[:project_name] New message for assignment '':object_name'' ', '<p><a href=":created_by_url">:created_by_name</a> has replied to assignment <a href=":object_url">:object_name</a> </p>\r\n\r\n<hr />\r\n\r\n:comment_body\r\n<hr />\r\n\r\n<p><a href=":comment_url">Click here</a> to respond to this message. \r\n\r\n\r\n\r\n<p>Thank You,<br />:owner_company_name</p>', 'owner_company_name\nproject_name\nproject_url\nobject_type\nobject_name\nobject_body\nobject_url\ncomment_body\ncomment_url\ncreated_by_url\ncreated_by_name\ndetails_body'),
('new_discussion', 'discussions', '[:project_name] Discussion '':object_name'' has been started', '<p>Hello,</p>\r\n<p><a href=":created_by_url">:created_by_name</a> has started a new discussion:</p>\r\n\r\n:details_body\r\n\r\n<p>Thank you,<br />:owner_company_name</p>', 'owner_company_name\nproject_name\nproject_url\nobject_type\nobject_name\nobject_body\nobject_url\ncreated_by_name\ncreated_by_url\nlast_comment_body\ndetails_body'),
('new_file', 'files', '[:project_name] File '':object_name'' has been uploaded', '<p>Hello,</p>\r\n\r\n<p><a href=":created_by_url">:created_by_name</a> uploaded a new file:</p>\r\n\r\n:details_body\r\n\r\n<p>Thank You,<br />:owner_company_name</p>', 'owner_company_name\nproject_name\nproject_url\nobject_type\nobject_name\nobject_body\nobject_url\ncreated_by_name\ncreated_by_url\ndetails_body'),
('new_revision', 'files', '[:project_name] New version of '':object_name'' file is up', '<p>Hello,</p>\r\n<p><a href=":created_by_url">:created_by_name</a> has uploaded a new version of <a href=":object_url">:object_name</a> file in <a href=":project_url">:project_name</a> project.</p>\r\n<p>Thank You,<br />:owner_company_name</p>', 'owner_company_name\nproject_name\nproject_url\nobject_type\nobject_name\nobject_body\nobject_url\ncreated_by_url\ncreated_by_name'),
('new_task_admin_notification', 'resources', 'New :object_type needs a tech', ':submitter has submitted a new :object_type. Log in and click the link below to assign to a tech:\n\n:object_url', 'object_type\r\nobject_url\r\nsubmitter'),
('new_task_confirmation', 'resources', 'New :object_type has been received', '<p>Hello,</p>\n\n<p>Your :object_type has been received.  Your :object_type will be reviewed and assigned to a Tech.</p>\n\n<p>Thank You,<br />:owner_company_name</p>', 'object_type\r\nowner_company_name'),
('new_user', 'system', 'Your account has been created', '<p>Hello,</p>\r\n\r\n<p><a href=":created_by_url">:created_by_name</a> has created a new account for you. You can <a href=":login_url">log in</a> with these parameters:</p>\r\n\r\n<p>Email: '':email'' (without quotes)<br />Password: '':password'' (without quotes)</p>\r\n\r\n<hr />\r\n\r\n<p>:welcome_body</p>\r\n\r\n<hr />\r\n\r\n<p>Thank you,<br />:owner_company_name</p>', 'owner_company_name\ncreated_by_id\ncreated_by_name\ncreated_by_url\nemail\npassword\nlogin_url\nwelcome_body'),
('reminder', 'system', '[:project_name] New assignment has been posted', '<p>Hello,</p>\r\n\r\n    <p><a href=":reminded_by_url">:reminded_by_name</a> wants you to check out <a href=":object_url">:object_name</a> :object_type. Comment:</p>\r\n\r\n    <hr />\r\n\r\n    <p>:comment_body</p>\r\n\r\n    <hr />\r\n\r\n    <p>Thank You,<br />:owner_company_name</p>', 'owner_company_name\nreminded_by_name\nreminded_by_url\nobject_name\nobject_url\nobject_type\ncomment_body\nproject_name\nproject_url'),
('task_assigned', 'resources', '[:project_name] New :object_type has been posted', '<p>Hello,</p>\n\n<p><a href=":created_by_url">:created_by_name</a> created a new :object_type:</p>\n\n:details_body\n\n<p>Thank You,<br />:owner_company_name</p>', 'owner_company_name\nproject_name\nproject_url\nobject_type\nobject_name\nobject_body\nobject_url\ncreated_by_name\ncreated_by_url\ndetails_body'),
('task_completed', 'resources', '[:project_name] '':object_name'' :object_type has been completed', '<p>Hello,</p>\r\n\r\n<p><a href=":completed_by_url">:completed_by_name</a> has completed :object_type <a href=":object_url">:object_name</a>:</p>\r\n\r\n:details_body\r\n\r\n<p>Thank You,<br />:owner_company_name</p>', 'owner_company_name\nproject_name\nproject_url\nobject_type\nobject_name\nobject_body\nobject_url\ncreated_by_name\ncreated_by_url\ncompleted_by_name\ncompleted_by_url\ndetails_body'),
('task_completed_with_comment', 'resources', '[:project_name] '':object_name'' :object_type has been completed', '<p>Hello,</p>\r\n\r\n<p><a href=":completed_by_url">:completed_by_name</a> has completed :object_type <a href=":object_url">:object_name</a> with a comment:</p>\r\n\r\n<hr />\r\n\r\n:completion_comment_body\r\n\r\n<hr />\r\n\r\n<p><a href=":object_url">:object_name</a> :object_type details:</p>\r\n:details_body\r\n\r\n<p>Thank You,<br />:owner_company_name</p>', 'owner_company_name\nproject_name\nproject_url\nobject_type\nobject_name\nobject_body\nobject_url\ncreated_by_name\ncreated_by_url\ncompleted_by_name\ncompleted_by_url\ncompletion_comment_body\ndetails_body'),
('task_edited', 'resources', '[:project_name] :object_type has been edited', '<p>Hello,</p>\r\n\r\n<p><a href=":created_by_url">:created_by_name</a> edited a :object_type:</p>\r\n\r\n:details_body\r\n\r\n<p>Thank You,<br />:owner_company_name</p>', 'owner_company_name\r\nproject_name\r\nproject_url\r\nobject_type\r\nobject_name\r\nobject_body\r\nobject_url\r\ncreated_by_name\r\ncreated_by_url\r\ndetails_body'),
('task_reassigned', 'resources', '[:project_name] '':object_name'' :object_type reassigned', '<p>Hello,</p>\r\n\r\n<p>We have an update that you might be interested in: :object_type <a href=":object_url">:object_name</a> has been updated. Changes:\r\n\r\n:changes_body\r\n\r\n<p>Thank You,<br />:owner_company_name</p>', 'owner_company_name\nproject_name\nproject_url\nobject_type\nobject_name\nobject_body\nobject_url\nchanges_body'),
('task_reopened', 'resources', '[:project_name] '':object_name'' :object_type has been reopened', '<p>Hello,</p>\r\n\r\n<p><a href=":reopened_by_url">:reopened_by_name</a> has reopened :object_type <a href=":object_url">:object_name</a>:</p>\r\n\r\n:details_body\r\n\r\n<p>Thank You,<br />:owner_company_name</p>', 'owner_company_name\nproject_name\nproject_url\nobject_type\nobject_name\nobject_body\nobject_url\ncreated_by_name\ncreated_by_url\nreopened_by_name\nreopened_by_url\ndetails_body'),
('tech_assigned_for_tech', 'resources', 'You have been assigned to an :object_type', '<p>Hello,</p>\r\n<p>You have been assigned to an :object_type for :company_name:</p>\r\n<p>:object_name</p>', 'object_type\r\nobject_name\r\ncompany_name'),
('tech_assigned_for_user', 'resources', 'Your :object_type has been assigned a technician', '<p>Hello,</p>\r\n<p>Your :object_type has been assigned to :tech_name and will be complete within 24 hours. Please login to view or edit your :object_type.</p>', 'object_type\r\ntech_name');
